#include "../../include/philo.h"

int is_all_digit(char *str) {
    int i = 0;

    if (str[i] == '+') {
        i++;
    }

    while (str[i]) {
        if (!(str[i] >= '0' && str[i] <= '9')) {
            return 0;
        }
        i++;
    }

    return 1;
}
